const initialState = {
    primary: 'rgb(220, 60, 20)',
};

export default function userThemeReducer(state = initialState, action) {
    switch(action.type) {
        case 'ThemeChange':
            return {
                ...state,
                primary: action.payload,
            };
        default:
            return state;
    }
}
