import React, { useState } from 'react';
import Card from '@material-ui/core/Card';
import CardHeader from '@material-ui/core/CardHeader';
import CardMedia from '@material-ui/core/CardMedia';
import CardContent from '@material-ui/core/CardContent';
import CardActions from '@material-ui/core/CardActions';
import Button from '@material-ui/core/Button';
import Avatar from '@material-ui/core/Avatar';
import Typography from '@material-ui/core/Typography';
import Collapse from '@material-ui/core/Collapse';
import IconButton from '@material-ui/core/IconButton';
import ExpandMoreIcon from '@material-ui/icons/ExpandMore';
import ExpandLessIcon from '@material-ui/icons/ExpandLess';
import HeartIcon from '@material-ui/icons/Favorite';
import ShareIcon from '@material-ui/icons/Share';
import MoreIcon from '@material-ui/icons/MoreVert';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemAvatar from '@material-ui/core/ListItemAvatar';
import ListItemText from '@material-ui/core/ListItemText';
import Divider from '@material-ui/core/Divider';
import { makeStyles } from '@material-ui/core/styles';
import Meghan from '../site-images/meghan.jpg';
import Abbey from '../site-images/abbey.jpg';
import Simeon from '../site-images/simeon.jpg';
import Jessica from '../site-images/jessica.jpg';

const useStyles = makeStyles((theme) => ({
    card: {
        maxWidth: 450,
        margin: 'auto',
        padding: theme.spacing(2),
    },
    media: {
        height: 0,
        padding: '56.25%',
    },
}));

export default function CardExample() {
    const classes = useStyles();
    const [show, setExpanded] = useState(false);

    function toggleExpanded() {
        setExpanded(!show);
    }

    return (
        <Card 
            className={classes.card} 
        >
            <CardHeader 
                avatar={
                    <Avatar 
                        src={Abbey}
                        alt='Abbey Fisher' 
                    />
                }
                title='Abbey Fisher' 
                subheader='December 7, 2020' 
                action={
                    <MoreIcon />
                }
            />
            <CardMedia 
                image={Abbey}
                title='Abbey Fisher' 
                className={classes.media}
            />
            <CardContent>
                <Typography 
                    variant='body1'
                    component='p'
                    color='textSecondary' 
                >
                    I am just trying to live my life the best I can and enjoy it from day to day. 
                </Typography>
            </CardContent>
            <CardActions>
                <IconButton>
                    <HeartIcon />
                </IconButton>
                <IconButton>
                    <ShareIcon />
                </IconButton>
                <IconButton 
                    style={{
                        marginLeft: 'auto',
                    }}
                    onClick={toggleExpanded}
                >
                    {show ? <ExpandLessIcon /> : <ExpandMoreIcon />}
                </IconButton>
            </CardActions>
            <Divider />
            <Collapse 
                in={show} 
            >
                <CardContent>
                    <List>
                        <ListItem>
                            <ListItemAvatar>
                                <Avatar 
                                    src={Simeon} 
                                    alt='Simeon Ikudabo' 
                                />
                            </ListItemAvatar>
                            <ListItemText 
                                primary='Simeon Ikudabo'
                                secondary={
                                    <React.Fragment>
                                        <Typography 
                                            variant='subtitle1' 
                                            component='span' 
                                            color='textSecondary' 
                                        >
                                            December 7, 2020
                                        </Typography>
                                        <Typography 
                                            variant='body1' 
                                        >
                                            You are so beautiful! I am blessed to have you!
                                        </Typography>
                                    </React.Fragment>
                                }
                            />
                        </ListItem>
                        <Divider />
                        <ListItem>
                            <ListItemAvatar>
                                <Avatar 
                                    src={Meghan} 
                                    alt='Meghan Gulvas' 
                                />
                            </ListItemAvatar>
                            <ListItemText 
                                primary='Meghan Gulvas' 
                                secondary={
                                    <React.Fragment>
                                        <Typography 
                                            variant='subtitle1'
                                            component='span' 
                                            color='textSecondary' 
                                        >
                                            Awe... You are so cute, and your boyfriend is so sweet!
                                        </Typography>
                                    </React.Fragment>
                                }
                            />
                        </ListItem>
                        <Divider/>
                        <ListItem>
                            <ListItemAvatar>
                                <Avatar 
                                    src={Jessica} 
                                    alt='Jessica Dub' 
                                />
                            </ListItemAvatar>
                            <ListItemText 
                                primary='Jessica Dub' 
                                secondary={
                                    <React.Fragment>
                                        <Typography 
                                            variant='subtitle1' 
                                            component='span' 
                                            color='textSecondary' 
                                        >
                                            December 14, 2020
                                        </Typography>
                                        <Typography 
                                            variant='body1' 
                                            component='p' 
                                        >
                                            This is so sweet! I can't wait to see you guys soon! 
                                            We are gonna hit up Kilroys!
                                        </Typography>
                                    </React.Fragment>
                                }
                            />
                        </ListItem>
                    </List>
                </CardContent>
            </Collapse>
        </Card>
    );
}
