const initialState = {
    primary: 'rgb(0, 20, 60)',
    mode: 'light',
};

export default function userThemeReducer(state = initialState, action) {
    switch(action.type) {
        case 'ThemeChange':
            return {
                ...state,
                primary: action.payload,
            };
        case 'ModeChange':
            return {
                ...state,
                mode: action.payload,
            };
        default:
            return state;
    }
}
