import React, { useState } from 'react';
import Card from '@material-ui/core/Card';
import CardHeader from '@material-ui/core/CardHeader';
import CardMedia from '@material-ui/core/CardMedia';
import CardContent from '@material-ui/core/CardContent';
import CardActions from '@material-ui/core/CardActions';
import IconButton from '@material-ui/core/IconButton';
import Collapse from '@material-ui/core/Collapse';
import Typography from '@material-ui/core/Typography';
import Avatar from '@material-ui/core/Avatar';
import MoreIcon from '@material-ui/icons/MoreVert';
import FavoriteIcon from '@material-ui/icons/Favorite';
import ShareIcon from '@material-ui/icons/Share';
import ExpandMoreIcon from '@material-ui/icons/ExpandMore';
import ExpandLessIcon from '@material-ui/icons/ExpandLess'; 
import { makeStyles } from '@material-ui/core/styles';
import Abbey from '../site-images/abbey.jpg';

const useStyles = makeStyles((theme) => ({
    card: {
        margin: 'auto',
        maxWidth: 450,
    },
    media: {
        height: 0,
        padding: '56.25%',
    },
}));

export default function Cards() {
    const classes = useStyles();
    const [show, setShow] = useState(false);

    function toggleShow() {
        setShow(!show);
    }

    return (
        <Card 
            className={classes.card} 
        >
            <CardHeader 
                title={
                    <Typography 
                        variant='h5' 
                        component='h5' 
                    >
                        Abbey Fisher
                    </Typography>
                }
                subtitle={
                    <Typography 
                        variant='subtitle1'
                        component='span'
                        color='textSecondary' 
                    >
                        December 7, 2020
                    </Typography>
                }
                action={
                    <IconButton>
                        <MoreIcon />
                    </IconButton>
                }
                avatar={
                    <Avatar 
                        alt='Abbey Fisher'
                        title='Abbey Fisher' 
                        aria-label='Profile avatar' 
                        src={Abbey} 
                    />
                }
            />
            <CardMedia 
                className={classes.media} 
                title='Abbey Fisher' 
                alt='Abbey Fisher' 
                image={Abbey} 
            />
            <CardContent>
                <Typography 
                    variant='body1'
                    component='p' 
                >
                    When I moved to Chicago last year I had a lot of fears and concerns like 
                    anyone else moving to a new city. However, I learned what it means to 
                    truly care about people and overcome challenges. 2020 has been a crazy 
                    and unpredictable year, but I have learned what it takes to live my 
                    best life and overcome challenges.
                </Typography>
            </CardContent>
            <CardActions>
                <IconButton>
                    <FavoriteIcon />
                </IconButton>
                <IconButton>
                    <ShareIcon />
                </IconButton>
                <IconButton 
                    style={{
                        marginLeft: 'auto',
                    }}
                >
                    {show ? <ExpandLessIcon onClick={toggleShow}/> : <ExpandMoreIcon onClick={toggleShow}/>}
                </IconButton>
            </CardActions>
            <Collapse 
                in={show} 
            >
                <CardContent>
                    <Typography 
                        variant='body1' 
                    >
                        This is just some extra content that needs to be shown!
                    </Typography>
                </CardContent>
            </Collapse>
        </Card>
    );
}
