import React, { useState, useRef, useEffect } from 'react';
import Card from '@material-ui/core/Card';
import CardHeader from '@material-ui/core/CardHeader';
import CardMedia from '@material-ui/core/CardMedia';
import CardContent from '@material-ui/core/CardContent';
import CardActions from '@material-ui/core/CardActions';
import Avatar from '@material-ui/core/Avatar';
import Typography from '@material-ui/core/Typography';
import Collapse from '@material-ui/core/Collapse';
import { ValidatorForm, TextValidator } from 'react-material-ui-form-validator';
import Button from '@material-ui/core/Button';
import Divider from '@material-ui/core/Divider';
import IconButton from '@material-ui/core/IconButton';
import Icon from '@mdi/react';
import { mdiThumbUp, mdiThumbUpOutline, mdiComment, mdiCommentOutline, mdiChevronDown, mdiChevronUp, mdiDelete } from '@mdi/js';
import { makeStyles } from '@material-ui/core/styles';
import axios from 'axios';
import * as _ from 'underscore';
import { connect } from 'react-redux';

const useStyles = makeStyles((theme) => ({
    card: {
        maxWidth: 600,
        margin: 'auto',
        padding: theme.spacing(2),
        backgroundColor: theme.palette.background.paper,
    },
}));

function UserPostsCard(props) {
    const classes = useStyles(); //Custom styles for the component
    const formRef = useRef(null); //Ref for the validator form
    const [expanded, setExpanded] = useState(false); //This expands the validator form

    return (
        <Card 
            className={classes.card} 
        >
            <CardHeader 
                title={
                    <Typography 
                        variant='h6' 
                        component='h6' 
                    >
                        {props.post.username}
                    </Typography>
                }
                subheader={
                    <Typography 
                        variant='subtitle1' 
                        component='span' 
                        color='textSecondary' 
                    >
                        {props.post.dateString}
                    </Typography>
                }
                avatar={
                    <Avatar 
                        src={`http://192.168.0.9:3001/api/get/avatar/by/id/${props.post.uniqueUserId}`}
                        alt={`${props.post.username}`}
                        title={`${props.post.username}`} 
                    />
                }
            />
            <CardContent>
                <Typography 
                    variant='body2' 
                    component='p' 
                >
                    {props.post.text}
                </Typography>
            </CardContent>
            <CardActions>
                <IconButton 
                    color='primary'
                >
                    <Icon  
                        path={props.post.likes.includes(props.mainUser) ? mdiThumbUpOutline : mdiThumbUp}
                        size={1} 
                        title='Post like button'
                        aria-label='Post like button' 
                    />
                </IconButton>
                <IconButton 
                    color='primary'
                >
                    <Icon 
                        path={_.find(props.post.comments, comment => comment.uniqueUserId === props.mainUser.uniqueUserId) ? mdiComment : mdiCommentOutline} 
                        size={1}
                        title='Comment button' 
                        aria-label='Comment button' 
                    />
                </IconButton>
                <div>
                    {props.post.uniqueUserId === props.mainUser.uniqueUserId &&
                        <IconButton 
                            color='primary' 
                        >
                            <Icon 
                                path={mdiDelete} 
                                size={1} 
                                title='Delete button' 
                                aria-label='Delete button' 
                            />
                        </IconButton>
                    }
                </div>
                <IconButton 
                    style={{
                        marginLeft: 'auto' 
                    }}
                    onClick={() => setExpand(!expanded)}
                >
                    <Icon 
                        path={expanded ? mdiChevronUp : mdiChevronDown} 
                        size={1}
                        title={expanded ? 'Expand less' : 'Expand more'} 
                        aria-label={expanded ? 'Expand less' : 'Expand more'} 
                    />
                </IconButton>
            </CardActions>
        </Card>
    );
}

function mapStateToProps(state) {
    return {
        mainUser: state.userReducer.user,
        posts: state.userPostsReducer.posts,
    };
}

export default connect(mapStateToProps)(UserPostsCard);
