import React, { useState, useEffect, useRef}  from 'react';
import { useHistory, useParams } from 'react-router-dom';
import Grid from '@material-ui/core/Grid';
import Avatar from '@material-ui/core/Avatar';
import Typography from '@material-ui/core/Typography';
import Button from '@material-ui/core/Button';
import Tabs from '@material-ui/core/Tabs';
import Tab from '@material-ui/core/Tab';
import Dialog from '@material-ui/core/Dialog';
import DialogContent from '@material-ui/core/DialogContent';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import { connect } from 'react-redux';
import axios from 'axios';
import * as _ from 'underscore';
import swal from 'sweetalert';
import { makeStyles } from '@material-ui/core/styles';
import Hidden from '@material-ui/core/Hidden';
import Icon from '@mdi/react';
import { mdiCheckBold, mdiPencil, mdiClose, mdiCamera, mdiVideo, mdiTwitter, mdiInstagram, mdiYoutube } from '@mdi/js';
import Paper from '@material-ui/core/Paper';
import PropTypes from 'prop-types';
import ListItem from '@material-ui/core/ListItem';
import ListItemText from '@material-ui/core/ListItemText';
import ListItemIcon from '@material-ui/core/ListItemIcon';
import Divider from '@material-ui/core/Divider';
import Link from '@material-ui/core/Link';

function TabPanel(props) {
    //This component will serve as the panel for each individual tab.
    const { children, value, index, ...other } = props;

    return (
        <div 
            role='tabpanel'
            hidden={index !== value} 
            id={`tabpanel-${index}`}
            aria-label={`tab-${index}`}
            aria-labelledby={`tab-${index}`}
            {...other}
            style={{
                marginTop: 100,
            }}
        >
            {value === index &&
                <Grid 
                    item 
                    xs
                    style={{
                        textAlign: 'center',
                    }}
                >
                    {children}
                </Grid>
            }
        </div>
    );
}

TabPanel.propTypes = {
    children: PropTypes.node,
    value: PropTypes.any.isRequired,
    index: PropTypes.any.isRequired,
}

function a11yProps(index) {
    return {
        id: `tab-${index}`,
        'aria-controls': `tabpanel-${index}`,
    };
}

function GeoUser(props) {
    const [geoUser, setGeoUser] = useState({
        profileTheme: 'rgb(220, 20, 60)',
        avatar: '49403404i0340i403i03',
    }); //This is the variable and setter that will set the value of the user for the page we are visiting. 
    const useStyles = makeStyles((theme) => ({
        root: {
            marginTop: 20,
        },
        profileAvatar: {
            height: 128,
            width: 128,
            margin: 'auto',
        },
        profileAvatarLg: {
            height: 168,
            width: 168,
            margin: 'auto',
        },
        usernameDisplay: {
            marginTop: 40,
            margin: 'auto',
        },
        statsInfo: {
            marginTop: 40,
            textAlign: 'center',
        },
        paper: {
            backgroundColor: geoUser ? geoUser.profileTheme : 'rgb(0, 20, 60)',
            color: 'rgb(255, 255, 255)',
            marginTop: 40,
        },
        input: {
            display: 'none',
        },
    }));
    
    const classes = useStyles();
    const history = useHistory();
    const params = useParams();
    const [curTab, setCurTab] = useState(0); //This is the variable and setter for the current tab that we will see based on "posts" "about" and "events".
    const [posts, setPosts] = useState([]); //This is the variable and the setter for the current posts for the GeoCities user who's page another user is visiting. 

    useEffect(() => {
        //Check if the state for the global user exists. If not, we will push this user back to the login page.
        if(!props.mainUser) {
            //TODO: Will need to create a seperate route to handle a user who isn't on GeoCities who might visit a page.
            history.goBack(1);
        }
        else if(geoUser.uniqueUserId === props.mainUser.uniqueUserId) {
            history.push('/profile');
        }
        else if(props.mainUser) {
            //We will grab the user of the current profile that we want to see, along with retrieving and updating the value of the current user. 
            return axios({
                method: 'GET',
                url: `http://192.168.0.9:3001/api/get/geo/user/${params.uniqueUserId}/${props.mainUser.uniqueUserId}`,
            }).then(response => {
                if(response.data.geoUser) {
                    alert('Fetched user');
                    console.log(response.data.geoUser);
                    //TODO: set up functionality to take the mainUser back one page if they are blocked.
                    setGeoUser(response.data.geoUser); //set the geoUser state var to the response user. 
                    setPosts(response.data.posts); //Set the local posts state variable to the geoUsers posts.
                    props.dispatch({type: 'user/updateUser', payload: response.data.mainUser}); //Set the global main user variable. 
                     //Change the theme of the page to that of the geoUser. 
                }
                else {
                    swal(
                        'Uh Oh!',
                        'There was an error finding that user! That user may no longer exist.',
                        'error',
                    );
                    history.goBack(1);
                }
            }).catch(err => {
                console.log(err.message);
                swal(
                    'Uh Oh!',
                    'There was an error finding that user! Please try again.',
                    'error',
                );
                history.goBack(1);
            });
        }
    }, []);

    return (
        <Grid 
            className={classes.root} 
        >
            <Grid 
                item 
                container
                xs={12} 
            >
                <Grid 
                    item 
                    xs={12} 
                >
                    {/* Below is the geoUser avatar that will be 128x128 pixels */}
                    <Hidden 
                        mdUp 
                    >
                        <Avatar 
                            className={classes.profileAvatar} 
                            src={geoUser ? `http://192.168.0.9:3001/api/get-photo/${geoUser.avatar}` : ''}
                            title={`${geoUser.username}`}
                            alt={`${geoUser.username}`} 
                        />
                    </Hidden>
                    {/* Below will display the avatar on medium screen and up devices */}
                    <Hidden 
                        smDown 
                    >
                        <Avatar 
                            className={classes.profileAvatarLg} 
                            src={geoUser ? `http://192.168.0.9:3001/api/get-photo/${geoUser.avatar}` : ''} 
                            title={`${geoUser.username}`}
                            alt={`${geoUser.username}`} 
                        />
                    </Hidden>
                    <Typography 
                        variant='h4' 
                    >
                        {geoUser ? geoUser.username : 'haha'}
                    </Typography>
                </Grid>
            </Grid>
        </Grid>
    );
}

function mapStateToProps(state) {
    //Retrieve the main global user along with the theme state variable to change to theme to the one the geoUser has set.
    return {
        mainUser: state.userReducer.user,
        primary: state.userThemeReducer.primary,
    };
}

export default connect(mapStateToProps)(GeoUser);
