import React, { useState, useEffect, useRef } from 'react';
import Grid from '@material-ui/core/Grid';
import Avatar from '@material-ui/core/Avatar';
import Typography from '@material-ui/core/Typography';
import { connect } from 'react-redux';
import axios from 'axios';
import { useHistory } from 'react-router-dom';
import swal from 'sweetalert';
import { makeStyles } from '@material-ui/core/styles';
import Hidden from '@material-ui/core/Hidden';
import Box from '@material-ui/core/Box';
import Icon from '@mdi/react';
import { mdiCheckBold, mdiPencil, mdiClose, mdiCamera } from '@mdi/js';
import Button from '@material-ui/core/Button';
import Dialog from '@material-ui/core/Dialog';
import DialogContent from '@material-ui/core/DialogContent';
import AppBar from '@material-ui/core/AppBar';
import Paper from '@material-ui/core/Paper';
import Tab from '@material-ui/core/Tab';
import Tabs from '@material-ui/core/Tabs';
import PropTypes from 'prop-types';
import TextField from '@material-ui/core/TextField';
import Backdrop from '@material-ui/core/Backdrop';
import CircularProgress from '@material-ui/core/CircularProgress';
import UserPostsCard from './UserPostsCard';

function TabPanel(props) {
    //This component will serve as the panel for each individual tab
    const { children, value, index, ...other } = props;

    return (
        <div 
            hidden={value !== index}
            role='tabpanel' 
            id={`tabpanel-${index}`}
            aria-label={`tab-${index}`}
            aria-labelledby={`tab-${index}`}
            {...other}
            style={{
                marginTop: 100,
            }}
        >
            {value === index && 
                <Grid 
                    item 
                    xs={12} 
                    style={{
                        textAlign: 'center',
                    }}
                >
                    {children}
                </Grid>
            }
        </div>
    );
}

TabPanel.propTypes = {
    children: PropTypes.node,
    value: PropTypes.any.isRequired,
    index: PropTypes.any.isRequired,
};

function a11yProps(index) {
    return {
        id: `tab-${index}`,
        'aria-controls': `tabpanel-${index}`,
    };
}



function ProfilePage(props) {
    const useStyles = makeStyles((theme) => ({
        root: {
            marginTop: 20,
        },
        profileAvatar: {
            height: 128,
            width: 128,
            margin: 'auto',
        },
        profileAvatarLg: {
            height: 168,
            width: 168,
            margin: 'auto',
        },
        usernameDisplay: {
            marginTop: 40,
            margin: 'auto',
        },
        statsInfo: {
            marginTop: 40,
            textAlign: 'center',
        },
        paper: {
            backgroundColor: props.user.profileTheme,
            color: 'rgb(255, 255, 255)',
            marginTop: 40,
        },
        input: {
            display: 'none',
        },
    }));
    const history = useHistory(); //The history API to access the brower history. 
    const classes = useStyles(); //Custom styling.
    const [openDialog, setOpenDialog] = useState(false); //Variable and setter to open and close dialog.
    const [curTab, setTab] = useState(0); //Variable and setter to control proper tab.
    const [curTab2, setTab2] = useState(0);
    const [textPost, setTextPost] = useState(''); //Variable and setter for text post control
    const [posting, setPosting] = useState(false); //Hides dialog AppBar and tabs when a post is posting to the server
    const [posts, setPosts] = useState([]); //Personal profile posts for the user. 
    const [uploadPhoto, setUploadPhoto] = useState(null); // Variable and setter for a photo to be uploaded

    
    useEffect(() => {
        //First, if the user doesn't exist in state, re-route to the login page.
        if(!props.user) {
            history.push('/');
        }
        else if(props.user) {
            //Else, we make an axios call to request the user (an updated version of the user)
            //props.dispatch({type: 'ThemeChange', payload: props.user.profileTheme});
            return axios({
                method: 'GET',
                url: `http://192.168.0.9:3001/api/grab/user/${props.user.uniqueUserId}`,
            }).then(response => {
                if(response.data.user) {
                    props.dispatch({type: 'user/updateUser', payload: response.data.user});
                    props.dispatch({type: 'ThemeChange', payload: response.data.user.profileTheme});
                    props.dispatch({type: 'userPosts/updatePosts', payload: response.data.posts});
                }
                else if(response.data === 'user not found') {
                    swal(
                        'Uh Oh!',
                        'We could not find your profile!',
                        'error',
                    );
                    history.push('/');
                }
            }).catch(err => {
                console.log(err.message);
                swal(
                    'Uh Oh!',
                    'There was an error retreiving your profile. Please try again!',
                    'error',
                );
                history.push('/');
            });
        }
    }, []);

    function handleTextPost() {
        //Function that handles submitting a text post to the server. 
        setPosting(true);
        if(!textPost) {
            swal(
                'Uh Oh!',
                'You must enter some text',
                'error',
            );

            setPosting(false);
        }
        else {
            let months = ['Jan', 'Feb', 'Mar', 'Apr', 'May', 'Jun', 'Jul', 'Aug', 'Sept', 'Oct', 'Nov', 'Dec'];
            let date = new Date();
            let year = date.getFullYear();
            let month = months[date.getMonth()];
            let day = date.getDate();

            let data = JSON.stringify({
                uniquePostId: Date.now() + props.user.uniqueUserId + props.user.username,
                uniqueUserId: props.user.uniqueUserId,
                username: props.user.username,
                type: 'text', 
                context: 'personal',
                text: textPost,
                dateString: `${month} ${day}, ${year}`,
                link: '',
                title: '',
                community: '',
                caption: '',
                src: '',
                privacy: props.user.profilePrivacy,
            });

            return axios({
                method: 'POST',
                url: 'http://192.168.0.9:3001/api/upload/text/link/post',
                data: data,
                headers: {
                    'Content-Type': 'application/json',
                },
            }).then(response => {
                if(response.data.posts) {
                    swal(
                        'Great!',
                        'Post was successfully uploaded!',
                        'success',
                    );
                    setPosting(false);
                    setTextPost('');
                    setOpenDialog(false);
                    console.log(response.data.posts);
                    //setPosts(posts => [response.data.posts[0], ...posts]);
                    //Maybe altering the code to just push a new value to the array will work?
                    props.dispatch({type: 'userPosts/updatePosts', payload: response.data.posts});
                }
                else {
                    swal(
                        'Uh Oh!',
                        'There was an error uploading that post! Please try again.',
                        'error',
                    )
                    setPosting(false);
                }
            }).catch(err => {
                console.log(err.message);
                swal(
                    'Uh Oh!',
                    'There was an error uploading that post! Please try again.',
                    'error',
                );
                setPosting(false);
            });
        }
    }

    function handlePhotoChange(e) {
        setUploadPhoto(e.target.files[0]);
    }

    return (
        <Grid 
            className={classes.root}
            container 
        >
            <Grid 
                item 
                container 
                xs={12}
            >
                <Grid 
                    item 
                    xs={12}
                >
                    {/* Below is the avatar for xs and sm screens at 128x128 pixels */}
                    <Hidden 
                        mdUp 
                    >
                        <Avatar 
                            className={classes.profileAvatar} 
                            src={`http://192.168.0.9:3001/api/get-photo/${props.user.avatar}`} 
                            title={`${props.user.username}`} 
                            alt={`${props.user.username}`} 
                        />
                    </Hidden>
                    {/* Below will be the avatar for medium screen and up devices */ }
                    <Hidden 
                        smDown 
                    >
                        <Avatar 
                            className={classes.profileAvatarLg}
                            src={`http://192.168.0.9:3001/api/get-photo/${props.user.avatar}`} 
                            title={`${props.user.username}`} 
                            alt={`${props.user.username}`} 
                        />
                    </Hidden>
                </Grid>
            </Grid>
            <Grid 
                item 
                xs={12} 
            >
                <Typography 
                    variant='h4' 
                    component='h4' 
                    align='center'
                    className={classes.usernameDisplay} 
                >
                    {props.user.username}
                </Typography>
                {props.user.isVerified &&
                    <Typography 
                        variant='body1'
                        component='p' 
                    >
                        <Icon 
                            path={mdiCheckBold} 
                            size={1} 
                            title='Verified' 
                            style={{
                                color: 'rgb(0, 128, 0)'
                            }}
                        /> Verified 
                    </Typography>
                }
            </Grid>
            {/* Below will be the Grid to display Followers, Following, and Karma */}
            <Grid 
                item 
                container 
                xs={12} 
                spacing={1}
                className={classes.statsInfo}
            >
                <Grid 
                    item 
                    xs={4} 
                >
                    <Typography 
                        variant='h6' 
                        component='h6' 
                    >
                        Followers 
                    </Typography>
                    <Typography 
                        variant='body1' 
                        component='p' 
                    >
                        {props.user.followers.length}
                    </Typography>
                </Grid>
                <Grid 
                    item 
                    xs={4} 
                >
                    <Typography 
                        variant='h6' 
                        component='h6' 
                    >
                        Following 
                    </Typography>
                    <Typography 
                        variant='body1' 
                        component='p' 
                    >
                        {props.user.following.length}
                    </Typography>
                </Grid>
                <Grid 
                    item 
                    xs={4} 
                >
                    <Typography 
                        variant='h6' 
                        component='h6' 
                    >
                        Karma 
                    </Typography>
                    <Typography 
                        variant='body1' 
                        component='p' 
                    >
                        {props.user.karma}
                    </Typography>
                </Grid>
            </Grid>
            {/* End of the Grid for Followers, Following, and Karma */}
            {/* Below is the Grid to display the users' first and last name */}
            <Grid 
                item 
                xs={12} 
                style={{
                    textAlign: 'center',
                }}
            >
                <Typography 
                    variant='body1' 
                    component='p' 
                    style={{
                        fontWeight: 'bold',
                        marginTop: 30,
                    }}
                >
                    {props.user.firstName} {props.user.lastName}
                </Typography>
            </Grid>
            {/* End of the Grid to display the users first and last name */}
            {/* Below is the Grid that stores the users BIO */}
            <Grid 
                item 
                xs={12} 
                style={{
                    marginTop: 40,
                    textAlign: 'center',
                }}
            >
                <Typography 
                    variant='body2' 
                    component='small' 
                    justify='start'
                    align='left'
                >
                    {props.user.bio}
                </Typography>
            </Grid>
            {/* This is the end of the Grid for the bio.*/ }
            {/* Below is the grid to display the Button that opens the Dialog for the form for posts */}
            <Grid 
                item 
                xs={12}
                style={{
                    textAlign: 'center',
                    marginTop: 40,
                }}
            >
                <Button 
                    color='primary' 
                    variant='outlined'  
                    size='large'
                    onClick={() => setOpenDialog(true)}
                    startIcon={
                        <Icon 
                            path={mdiPencil} 
                            size={1} 
                            title='Post Icon' 
                        />
                    }
                >
                    Post 
                </Button>
            </Grid>
            {/* The Grid below will store the Dialog and its' content */}
            <Grid 
                item 
                xs={12} 
            >
                <Dialog 
                    open={openDialog} 
                    fullScreen
                >
                    <DialogContent>
                        <AppBar 
                            color='primary'
                            hidden={posting}
                        >
                            <Icon 
                                path={mdiClose} 
                                size={1} 
                                title='Dialog close' 
                                style={{
                                    cursor: 'pointer'
                                }}
                                onClick={() => setOpenDialog(false)}
                            />
                            <Tabs 
                                value={curTab}
                                onChange={(e, newVal) => setTab(newVal)} 
                                centered
                            >
                                <Tab 
                                    {...a11yProps(0)} 
                                    label='Text' 
                                    disabled={posting}
                                />
                                <Tab 
                                    {...a11yProps(1)} 
                                    label='Photo' 
                                    disabled={posting}
                                />
                                <Tab 
                                    {...a11yProps(2)}
                                    label='Video' 
                                    disabled={posting}
                                />
                            </Tabs>
                        </AppBar>
                        <TabPanel 
                            index={0}
                            value={curTab}
                        >
                            <TextField 
                                label='Text Post' 
                                placeholder='Post a status!' 
                                color='primary'
                                variant='outlined' 
                                InputLabelProps={{
                                    shrink: true,
                                }}
                                aria-label='Text Post Field' 
                                rows={4} 
                                onChange={e => setTextPost(e.target.value)}
                                value={textPost} 
                                helperText='Enter a status update!' 
                                multiline
                                fullWidth 
                                disabled={posting}
                            />
                            <Button 
                                style={{
                                    marginTop: 20,
                                }}
                                color='primary' 
                                variant='outlined' 
                                disabled={posting}
                                onClick={handleTextPost}
                            >
                                {posting ? <CircularProgress /> : 'post'}
                            </Button>
                        </TabPanel>
                        <TabPanel 
                            index={1} 
                            value={curTab} 
                        >
                            <Typography 
                                variant='h6' 
                                component='h6' 
                            >
                                Post a photo
                            </Typography>
                            <label 
                                html-for='photoPost' 
                            >
                                <input 
                                    className={classes.input}
                                    type='file'
                                    accept='image/jpg, image/jpeg, image/png' 
                                    id='photoPost'
                                    name='photoPost' 
                                    onChange={handlePhotoChange} 
                                    required 
                                />
                                <Button 
                                    variant='contained' 
                                    color='primary' 
                                    component='span' 
                                    aria-label='Photo picker button'
                                >
                                    <Icon 
                                        path={mdiCamera} 
                                        size={1} 
                                        title='Photo upload icon' 
                                        aria-label='Photo upload icon' 
                                    />
                                </Button>
                                <br/>
                                <Button 
                                    style={{
                                        marginTop: 30,
                                    }}
                                    color='primary' 
                                    variant='outlined' 
                                >
                                    Upload
                                </Button>
                            </label>
                        </TabPanel>
                        <TabPanel 
                            index={2} 
                            value={curTab} 
                        >
                            Videos 
                        </TabPanel>
                    </DialogContent>
                </Dialog>
            </Grid>
            {/* Below will begin the tab panels for the posts, about, and events tabs */}
            <Grid 
                item 
                xs={12} 
            >
                <Paper 
                    className={classes.paper}
                >
                    <Tabs 
                        value={curTab2} 
                        onChange={(e, newVal) => setTab2(newVal)}
                        centered
                    >
                        <Tab 
                            label='Posts' 
                            {...a11yProps(0)} 
                        />
                        <Tab 
                            label='About' 
                            {...a11yProps(1)} 
                        />
                        <Tab 
                            label='Events' 
                            {...a11yProps(2)} 
                        />
                    </Tabs>
                </Paper>
                <Grid 
                    item 
                    xs={12}
                />
                <TabPanel 
                    value={0} 
                    index={curTab2} 
                >
                    {props.posts.length < 1 && 
                        <Typography 
                            variant='h6' 
                            component='h6' 
                            align='center' 
                        >
                            No posts 
                        </Typography>
                    }
                    {props.posts.length > 0 && 
                        <div>
                            {props.posts.map((post, index) => (
                                <div 
                                    style={{
                                        textAlign: 'left',
                                    }}
                                    key={index}
                                >
                                    <UserPostsCard 
                                        user={props.user} 
                                        post={post}
                                        key={index} 
                                        id={post.uniquePostId}
                                    />
                                    {index < props.posts.length - 1 &&
                                        <div 
                                            style={{
                                                marginBottom: 20,
                                            }}
                                        />
                                    }
                                </div>
                            ))}
                        </div>
                    }
                </TabPanel>
                <TabPanel 
                    value={1} 
                    index={curTab2} 
                >
                    About
                </TabPanel>
                <TabPanel 
                    value={2} 
                    index={curTab2} 
                >
                    Events
                </TabPanel>
            </Grid>
        </Grid>
    );
}

function mapStateToProps(state) {
    return {
        user: state.userReducer.user,
        primary: state.userThemeReducer.primary,
        posts: state.userPostsReducer.posts,
    };
}

export default connect(mapStateToProps)(ProfilePage);
