import React, { useState, useRef, useEffect } from 'react';
import Card from '@material-ui/core/Card';
import CardContent from '@material-ui/core/CardContent';
import CardActions from '@material-ui/core/CardActions';
import Box from '@material-ui/core/Box';
import TextField from '@material-ui/core/TextField';
import { ValidatorForm, TextValidator } from 'react-material-ui-form-validator';
import Typography from '@material-ui/core/Typography';
import Button from '@material-ui/core/Button';
import axios from 'axios';
import swal from 'sweetalert';
import Backdrop from '@material-ui/core/Backdrop';
import CircularProgress from '@material-ui/core/CircularProgress';
import { makeStyles } from '@material-ui/core/styles';

const useStyles = makeStyles((theme) => ({
    root: {
        textAlign: 'center',
        padding: theme.spacing(2),
    },
    card: {
        maxWidth: 450,
        margin: 'auto',
    },
    signUpBtn: {
        backgroundColor: 'rgb(255, 255, 255)',
        color: 'rgb(0, 32, 91)',
        marginLeft: 'auto',
    },
}));

export default function Login(props) {
    const classes = useStyles();
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [open, setOpen] = useState(false);
    const formRef = useRef(null);
    const regularExpressions = {
        usernameExpression: /^\w+$/,
        alphaStartRe: /^[A-Za-z]+$/,
    }

    useEffect(() => {
        //The validation rule below ensures a username and password have at least 6 characters.
        ValidatorForm.addValidationRule('enoughCharacters', value => {
            if(value.length < 6) {
                return false;
            }
            else {
                return true;
            }
        });

        //The validation rule below ensures that a username only containers letters, numbers and underscores.
        ValidatorForm.addValidationRule('usernameRe', value => {
            if(!regularExpressions.usernameExpression.test(value)) {
                return false;
            }
            else {
                return true;
            }
        });

        ValidatorForm.addValidationRule('alphaStartRe', value => {
            if(!regularExpressions.alphaStartRe.test(value.charAt(0))) {
                return false;
            }
            else {
                return true;
            }
        });

    }, []);

    function handleUsernameChange(e) {
        setUsername(e.target.value);
    }

    function handlePasswordChange(e) {
        setPassword(e.target.value);
    }

    function enterPress(e) {
        /*This function will handle an "enter" key press. If the user presses enter 
        while they are in the password TextField box, they will trigger the 
        sendLoginData function 
        */
        if(e.charCode === 13) {
            sendLoginData();
        }
    }

    function preventSpace(e) {
        //This function will prevent spaces when the user enters a username.
        //It is triggered with an onKeyDown event.
        if(e.keyCode === 32) {
            e.preventDefault();
            return;
        }
    }

    function sendLoginData() {
        //This function will handle sending the login data to the server with axios 
        console.log(formRef.current.isFormValid() === true);
        setOpen(true);
        if(username.length < 6) {
            swal(
                'Uh oh!',
                'Username must be at least 6 characters long!',
                'error',
            );
            setOpen(false);
            return;
        }
        else if(password.length < 6) {
            swal(
                'Uh oh!',
                'Password must be at least 6 characters long!',
                'error',
            );
            setOpen(false);
            return;
        }
        else {
            let data = JSON.stringify({
                username: username,
                password: password,
            });

            return axios({
                method: 'post',
                data: data,
                url: 'http://192.168.0.9:3001/api/login',
                headers: {
                    'Content-Type': 'application/json',
                }
            }).then(response => {
                if(response.data === 'success') {
                    setOpen(false);
                    swal(
                        'Great!',
                        'Successfully logged in',
                        'success',
                    );
                }
                else if(response.data === 'user not found') {
                    setOpen(false);
                    swal(
                        'Uh oh!',
                        'We could not find that username! Please try again. If you forgot your username, press the "FORGOT USERNAME OR PASSWORD" button below.',
                        'error',
                    );
                }
                else if(response.data === 'invalid password') {
                    setOpen(false);
                    swal(
                        'Uh oh!',
                        'That password was invalid. Please try again. If you forgot your password, press the "FORGOT USERNAME OR PASSWORD" button below.',
                        'error',
                    );
                }
            }).catch(err => {
                setOpen(false);
                console.log(err.message);
                swal(
                    'Uh oh!',
                    'There was an error logging you in. Please try again!',
                    'error',
                )
            });
        }
    }

    return (
        <div 
            className={classes.root} 
        >
            <Backdrop 
                open={open}
            >
                <CircularProgress 
                    color='primary' 
                />
                <Box 
                    mt={2} 
                >
                    <Typography 
                        variant='body1'
                        color='textSecondary' 
                        component='p' 
                    >
                        Logging in
                    </Typography>
                </Box>
            </Backdrop>
            <Card 
                className={classes.card}
                hidden={open}
            >
                <CardContent>
                    <Box 
                        mb={4} 
                    >
                        <Typography 
                            variant='h6'
                            component='h6' 
                        >
                            Welcome to Excite!
                        </Typography>
                        <Typography 
                            variant='subtitle1' 
                            component='span' 
                        >
                            Log in 
                        </Typography>
                    </Box>
                    <Box>
                        <ValidatorForm 
                            ref={formRef}
                            onSubmit={e => e.preventDefault()}
                        >
                            <Box 
                                mb={3}
                            >
                                <TextValidator
                                    variant='outlined' 
                                    label='Username'
                                    placeholder='Username'
                                    color='primary'
                                    value={username}
                                    onChange={handleUsernameChange}
                                    onKeyDown={preventSpace}
                                    type='username'
                                    helperText='Username must be at least 6 characters'
                                    validators={['enoughCharacters', 'usernameRe', 'alphaStartRe']}
                                    errorMessages={['Username must be at least 6 characters', 'Username can only contain letters, numbers and underscores', 'Username must start with a letter']}
                                    fullWidth
                                    required
                                />
                            </Box>
                            <TextValidator
                                variant='outlined' 
                                label='Password'
                                placeholder='Password'
                                color='primary'
                                value={password} 
                                onChange={handlePasswordChange}
                                onKeyPress={enterPress}
                                type='password' 
                                helperText='Password must be at least 6 characters'
                                validators={['enoughCharacters']}
                                errorMessages={['Password must be at least 6 characters']}
                                fullWidth 
                                required
                            />
                        </ValidatorForm>
                    </Box>
                </CardContent>
                <CardActions>
                    <Button 
                        color='primary' 
                        variant='contained' 
                        onClick={sendLoginData}
                    >
                        Log in 
                    </Button>
                    <Button 
                        className={classes.signUpBtn} 
                        variant='contained' 
                        style={{
                            marginLeft: 'auto',
                        }}
                    >
                        Sign up
                    </Button>
                </CardActions>
                <CardContent>
                    <Box>
                        <Button 
                            color='primary' 
                        >
                            Forgot username or password?
                        </Button>
                    </Box>
                </CardContent>
            </Card>
        </div>
    );
}
