import React, { useState } from 'react';
import Card from '@material-ui/core/Card';
import CardHeader from '@material-ui/core/CardHeader';
import CardMedia from '@material-ui/core/CardMedia';
import CardContent from '@material-ui/core/CardContent';
import CardActions from '@material-ui/core/CardActions';
import IconButton from '@material-ui/core/IconButton';
import Collapse from '@material-ui/core/Collapse';
import Typography from '@material-ui/core/Typography';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemAvatar from '@material-ui/core/ListItemAvatar';
import ListItemText from '@material-ui/core/ListItemText';
import Avatar from '@material-ui/core/Avatar';
import Grid from '@material-ui/core/Grid';
import Divider from '@material-ui/core/Divider';
import MoreIcon from '@material-ui/icons/MoreVert';
import ShareIcon from '@material-ui/icons/Share';
import FavoriteIcon from '@material-ui/icons/Favorite';
import ExpandMoreIcon from '@material-ui/icons/ExpandMore';
import ExpandLessIcon from '@material-ui/icons/ExpandLess';
import { makeStyles } from '@material-ui/core/styles';
import Simeon from '../site-images/simeon.jpg';
import Jessica from '../site-images/jessica.jpg';
import Meghan from '../site-images/meghan.jpg';

const useStyles = makeStyles((theme) => ({
    card: {
        maxWidth: 450,
        margin: 'auto',
    },
    media: {
        height: 0,
        padding: '56.25%',
    },
}));

export default function CardExample() {
    const classes = useStyles();
    const [show, setExpanded] = useState(false);

    function toggleExpanded() {
        setExpanded(!show);
    }

    return (
        <Card 
            className={classes.card} 
        >
            <CardHeader 
                avatar={
                    <Avatar 
                        src={Jessica} 
                        alt='Jessica' 
                    />
                }
                title='Jessica' 
                subheader='December 7, 2020' 
                action={
                    <IconButton>
                        <MoreIcon />
                    </IconButton>
                }
            />
            <CardMedia 
                image={Jessica} 
                alt='Jessica' 
                title='Jessica at the pool' 
                className={classes.media} 
            />
            <CardContent>
                <Typography 
                    variant='body1' 
                    color='textSecondary' 
                >
                    When you have rough days, it's always fun to just relax at the pool. 
                    I have focused more on my mental and physical health. I have been focused 
                    on getting things done. And I am thankful for my boyfriend who enables me to 
                    have some level of peace, comfort and joy with his support. Thanks!
                </Typography>
            </CardContent>
            <CardActions>
                <IconButton>
                    <FavoriteIcon />
                </IconButton>
                <IconButton>
                    <ShareIcon />
                </IconButton>
                <IconButton 
                    style={{
                        marginLeft: 'auto',
                    }}
                    onClick={toggleExpanded}
                >
                    {show ? <ExpandLessIcon /> : <ExpandMoreIcon />}
                </IconButton>
            </CardActions>
            <Collapse 
                in={show} 
            >
                <CardContent>
                    <List>
                        <ListItem>
                            <ListItemAvatar>
                                <Avatar 
                                    src={Simeon} 
                                    alt='Simeon' 
                                />
                            </ListItemAvatar>
                            <ListItemText 
                                primary='Simeon' 
                                secondary={
                                    <React.Fragment>
                                        <Typography 
                                            variant='subtitle1' 
                                            component='span' 
                                            color='textSecondary' 
                                        >
                                            December 7, 2020
                                        </Typography>
                                        <Typography 
                                            variant='body1'  
                                            component='p' 
                                        >
                                            Relaxation! That's the key. That's why I love going to 
                                            the pool too. Glad I can help you feel better!
                                        </Typography>
                                    </React.Fragment>
                                }
                            />
                            <Grid 
                                container 
                            >
                                <Grid 
                                    item 
                                    xs={6} 
                                >
                                    <IconButton>
                                        12 <FavoriteIcon />
                                    </IconButton>
                                </Grid>
                            </Grid>
                        </ListItem>
                        <Divider />
                        <ListItem
                            alignItems='flex-start' 
                        >
                            <ListItemAvatar>
                                <Avatar 
                                    src={Meghan} 
                                    alt='Meghan' 
                                />
                            </ListItemAvatar>
                            <ListItemText 
                                    primary='Meghan' 
                                    secondary={
                                        <React.Fragment>
                                            <Typography 
                                                variant='subtitle1' 
                                                component='span' 
                                                color='textSecondary' 
                                            >
                                                December 7, 2020
                                            </Typography>
                                            <Typography 
                                                variant='body1' 
                                                component='p' 
                                            >
                                                I am coming later! It's nice out and I have been 
                                                thinking about the pool all day!
                                            </Typography>
                                        </React.Fragment>
                                    }
                                />
                                <br></br>
                                <Grid 
                                    container 
                                >
                                    <Grid 
                                        item 
                                        xs={6} 
                                    >
                                        <IconButton>
                                            44 <FavoriteIcon />
                                        </IconButton>
                                    </Grid>
                                </Grid>
                        </ListItem>
                        <Divider />
                    </List>
                </CardContent>
            </Collapse>
        </Card>
    );
}
