import { persistReducer } from 'redux-persist';
import storage from 'redux-persist/lib/storage';

const config = {
    key: 'root',
    storage,
};

const initialState = {
    value: 0,
};

export default function counterReducer(state = initialState, action) {
    switch(action.type) {
        case 'counter/increment':
            return {
                state,
                value: state.value + 1,
            };
        case 'counter/decrement':
            return {
                state,
                value: state,
            }
        default:
            return state;
    }
}
