const initialState = {
    value: 0,
};

function counterReducer(state = initialState, action) {
    if(action.type === 'counter/increment') {
        return {
            state,
            value: state.value + 1,
        };
    }
    else {
        return state;
    }
}

module.exports = counterReducer;
