import React, { useState } from 'react';
import Card from '@material-ui/core/card';
import CardHeader from '@material-ui/core/CardHeader';
import CardMedia from '@material-ui/core/CardMedia';
import CardContent from '@material-ui/core/CardContent';
import CardActions from '@material-ui/core/CardActions';
import IconButton from '@material-ui/core/IconButton';
import Collapse from '@material-ui/core/Collapse';
import Avatar from '@material-ui/core/Avatar';
import Typography from '@material-ui/core/Typography';
import Icon from '@mdi/react';
import { mdiThumbUp, mdiThumbUpOutline, mdiComment, mdiCommentOutline, mdiDelete, mdiChevronDown, mdiChevronUp } from '@mdi/js';
import { makeStyles } from '@material-ui/core/styles';
import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button';
import CircularProgress from '@material-ui/core/CircularProgress';
import { connect } from 'react-redux';
import { useHistory } from 'react-router-dom';

const useStyles = makeStyles(() => ({
    card: {
        maxWidth: 600,
        margin: 'auto',
    },
    media: {
        height: 0,
        padding: '56.25%',
    }
}));

function TextPostCardP(props) {
    const classes = useStyles();
    const [expand, setExpand] = useState(false); //Expansion variable and setter for comments in Collapse
    const [commentVal, setCommentVal] = useState(''); //This will hold the value of a comment a user posts on a post.

    return (
        <Card 
            className={classes.card} 
        >
            <CardHeader 
                title={
                    <Typography 
                        variant='h6' 
                        component='h6' 
                    >
                        {props.user.username}
                    </Typography>
                }
                subheader={
                    <Typography 
                        variant='subtitle1' 
                        component='span' 
                        textColor='secondary' 
                    >
                        {props.post.dateString}
                    </Typography>
                }
                avatar={
                    <Avatar 
                        src={props.post.context === 'personal' ? `http://192.168.0.9:3001/api/get-photo/${props.user.avatar}` : 'path to community photo upload'}
                        title={props.user.username} 
                        alt={props.user.username}
                    />
                }
            />
            <CardContent>
                {props.post.text}
            </CardContent>
            <CardActions>
                <IconButton 
                    color='primary' 
                >
                    <Icon 
                        path={mdiThumbUpOutline} 
                        size={1} 
                        aria-label='Like Button' 
                    />
                    {props.post.likes.length}
                </IconButton>
                <IconButton 
                    color='primary' 
                >
                    <Icon 
                        path={mdiComment} 
                        size={1} 
                        aria-label='Comment Button' 
                    />
                    {props.post.comments.length}
                </IconButton>
                <IconButton 
                    color='primary' 
                >
                    <Icon 
                        path={mdiDelete} 
                        size={1} 
                        aria-label='Delete Icon' 
                    />
                </IconButton>
                <IconButton 
                    style={{
                        marginLeft: 'auto',
                    }}
                    onClick={() => setExpand(!expand)}
                >
                    <Icon 
                        path={expand ? mdiChevronUp : mdiChevronDown} 
                        size={1} 
                        aria-label={expand ? 'Close comments' : 'Expand comments'} 
                    />
                </IconButton>
            </CardActions>
            <Collapse 
                in={expand} 
            >
                <CardContent>
                    {props.mainUser !== null &&
                        <TextField 
                            label='Comment' 
                            placeholder='Comment...'
                            value={commentVal}
                            onChange={e => setCommentVal(e.target.value)}
                            rows={3} 
                            color='primary'
                            variant='outlined' 
                            rows={4} 
                            multiline
                            fullWidth 
                        />
                    }
                </CardContent>
            </Collapse>
        </Card>
    );
}

function mapStateToProps(state) {
    return {
        mainUser: state.userReducer.user,
    }
}

export default connect(mapStateToProps)(TextPostCardP);
