import React, { useState, useEffect } from 'react';
import Grid from '@material-ui/core/Grid';
import Avatar from '@material-ui/core/Avatar';
import Typography from '@material-ui/core/Typography';
import { connect } from 'react-redux';
import axios from 'axios';
import { useHistory } from 'react-router-dom';
import swal from 'sweetalert';
import { makeStyles } from '@material-ui/core/styles';
import Hidden from '@material-ui/core/Hidden';
import Box from '@material-ui/core/Box';
import Container from '@material-ui/core/Container';

const useStyles = makeStyles((theme) => ({
    root: {
        marginTop: 20,
    },
    profileAvatar: {
        height: 128,
        width: 128,
    },
    profileAvatarLg: {
        height: 168,
        width: 168,
    },
}));

function ProfilePage(props) {
    const history = useHistory(); //The history API to access the brower history. 
    const classes = useStyles(); //Custom styling.
    
    useEffect(() => {
        //First, if the user doesn't exist in state, re-route to the login page.
        if(!props.user) {
            history.push('/');
        }
        else if(props.user) {
            //Else, we make an axios call to request the user (an updated version of the user)
            //props.dispatch({type: 'ThemeChange', payload: props.user.profileTheme});
            return axios({
                method: 'GET',
                url: `http://192.168.0.9:3001/api/grab/user/${props.user.uniqueUserId}`,
            }).then(response => {
                if(response.data.user) {
                    props.dispatch({type: 'user/updateUser', payload: response.data.user});
                    props.dispatch({type: 'ThemeChange', payload: response.data.user.profileTheme});
                }
                else if(response.data === 'user not found') {
                    swal(
                        'Uh Oh!',
                        'We could not find your profile!',
                        'error',
                    );
                    history.push('/');
                }
            }).catch(err => {
                console.log(err.message);
                swal(
                    'Uh Oh!',
                    'There was an error retreiving your profile. Please try again!',
                    'error',
                );
                history.push('/');
            });
        }
    }, []);

    return (
        <Container 
            maxWidth='xl' 
            className={classes.root}
        >
            {/*Below is the Grid for the entire component */}
            <Grid 
                container 
            >
                {/*Below is the Grid for the Avatar and Username */}
                <Grid 
                    container 
                    item 
                    xs={12} 
                    justify='center'
                    alignItem='center'
                >
                    {/* Below is the Grid for the Avatar. 6 columns on mdUp, 12 on smDown */}
                    <Grid 
                        item 
                        xs={12}
                        md={6} 
                    >
                        {/* Hidden on medium screen devices and up */}
                        <Hidden 
                            mdUp
                        >
                            <Box 
                                mx='auto'
                                px='auto'
                            >
                                <Avatar 
                                    className={classes.profileAvatar} 
                                    src={`http://192.168.0.9:3001/api/get-photo/${props.user.avatar}`}
                                    alt={`${props.user.username} GeoCities avatar`}
                                    title={`${props.user.username} GeoCities avatar`} 
                                />
                            </Box>
                        </Hidden>
                        {/* Hidden on small devices and down */}
                        <Hidden 
                            smDown 
                        >
                            <Avatar 
                                className={classes.profileAvatarLg}
                                src={`http://192.168.0.9:3001/api/get-photo/${props.user.avatar}`}
                                alt={`${props.user.username} GeoCities avatar`}
                                title={`${props.user.username} GeoCities avatar`}
                            />
                        </Hidden>
                    </Grid>
                    {/* Below is the Grid for the username */}
                    <Grid 
                        item 
                        xs={12}
                        md={6}
                    >
                        <Typography 
                            variant='h6' 
                            component='h6' 
                            align='center'
                            justify='center'
                        >
                            {props.user.username}
                        </Typography>
                    </Grid>
                </Grid>
            </Grid>
        </Container>
    );
}

function mapStateToProps(state) {
    return {
        user: state.userReducer.user,
        primary: state.userThemeReducer.primary,
    };
}

export default connect(mapStateToProps)(ProfilePage);
