import React, { useState, useEffect } from 'react';
import { useHistory } from 'react-router-dom';
import { HashLink as Link } from 'react-router-hash-link';
import swal from 'sweetalert';
import axios from 'axios';
import { connect } from 'react-redux';
import Grid from '@material-ui/core/Grid';
import Card from '@material-ui/core/Card';
import CardHeader from '@material-ui/core/CardHeader';
import CardMedia from '@material-ui/core/CardMedia';
import CardContent from '@material-ui/core/CardContent';
import CardActions from '@material-ui/core/CardActions';
import Button from '@material-ui/core/Button';
import Avatar from '@material-ui/core/Avatar';
import Typography from '@material-ui/core/Typography';
import { makeStyles } from '@material-ui/core/styles';
import Backdrop from '@material-ui/core/Backdrop';
import CircularProgress from '@material-ui/core/CircularProgress';
import IconButton from '@material-ui/core/IconButton';
import Icon from '@mdi/react';
import { mdiThumbUp, mdiComment } from '@mdi/js';
import * as _ from 'underscore';
import Plyr from 'react-plyr';

const useStyles = (() => ({
    grid: {
        marginTop: 100,
    },
    card: {
        maxWidth: 600,
        margin: 'auto',
    },
    media: {
        height: 0,
        padding: '56.25%',
    },
    image: {
        maxWidth: '100%',
        maxHeight: '100%',
    },
}));

function Feed(props) {
    const classes = useStyles();
    const history = useHistory();
    const [posts, setPosts] = useState([]);

    useEffect(() => {
        if(props.user !== null) {
            return axios({
                method: 'GET', 
                url: 'http://192.168.0.17:3001/api/get/user/feed/posts',
            }).then(response => {
                props.dispatch({type: 'ThemeChange', payload: props.user.profileTheme});
                setPosts([...response.data.posts]);
            }).catch(err => {
                console.log(err.message);
                swal(
                    'Uh Oh!',
                    'There was an error rectrieving posts for the feed! Please try again.',
                    'error',
                );
                history.goBack(1);
            });
        }
        else {
            return axios({
                method: 'GET',
                url: 'http://192.168.0.17:3001/api/get/nonuser/feed/posts',
            }).then(response => {
                setPosts([...response.data.posts]);
            }).catch(err => {
                console.log(err.message);
                swal(
                    'Uh Oh!',
                    'There was an error retreiving the posts for the feed! Please try again.',
                    'error',
                );
                history.goBack(1);
            });
        }
    }, []);

    function timeDifference(date2, dateString) {
        //This function will return whether or not we display something like "2 hrs ago" "20 min ago" or the date string for a post
        let date1 = Date.now();
        let difference = date1 - date2;
        let daysDifference = Math.floor(difference/1000/60/60/24);
        let hoursDifference = Math.floor(difference/1000/60/60);
        let minutesDifference = Math.floor(difference/1000/60);
        let secondsDifference = Math.floor(difference/1000);
        if(daysDifference > 0) {
            return dateString;
        }
        else if(hoursDifference > 0 && hoursDifference < 23) {
            return `${hoursDifference} hr ago`;
        }
        else if(minutesDifference > 0 && minutesDifference < 60) {
            return `${minutesDifference} min ago`;
        }
        else if(minutesDifference === 0) {
            return `${secondsDifference} secs ago`
        }
        else {
            return dateString;
        }
    }

    if(props.user !== null) {
        if(posts.length > 0) {
            return (
                <Grid 
                    className={classes.grid} 
                    container
                >
                    <Grid 
                        item 
                        xs={12} 
                    >
                        {posts.map((post, index) => (
