import React, { useState, useRef, useEffect } from 'react';
import Card from '@material-ui/core/Card';
import CardHeader from '@material-ui/core/CardHeader';
import CardMedia from '@material-ui/core/CardMedia';
import CardContent from '@material-ui/core/CardContent';
import CardActions from '@material-ui/core/CardActions';
import Avatar from '@material-ui/core/Avatar';
import Typography from '@material-ui/core/Typography';
import Collapse from '@material-ui/core/Collapse';
import { ValidatorForm, TextValidator } from 'react-material-ui-form-validator';
import Button from '@material-ui/core/Button';
import Divider from '@material-ui/core/Divider';
import IconButton from '@material-ui/core/IconButton';
import CircularProgress from '@material-ui/core/CircularProgress';
import Icon from '@mdi/react';
import { mdiThumbUp, mdiThumbUpOutline, mdiComment, mdiCommentOutline, mdiChevronDown, mdiChevronUp, mdiDelete, mdiSend } from '@mdi/js';
import { makeStyles } from '@material-ui/core/styles';
import axios from 'axios';
import * as _ from 'underscore';
import { connect } from 'react-redux';
import swal from 'sweetalert';

const useStyles = makeStyles((theme) => ({
    card: {
        maxWidth: 600,
        margin: 'auto',
        padding: theme.spacing(2),
        backgroundColor: theme.palette.background.paper,
    },
}));

function UserPostsCard(props) {
    const classes = useStyles(); //Custom styles for the component
    const formRef = useRef(null); //Ref for the validator form
    const [expanded, setExpanded] = useState(false); //This expands the validator form
    const [comment, setComment] = useState('');
    const [commentSending, setCommentSending] = useState(false);

    useEffect(() => {
        ValidatorForm.addValidationRule('notEmpty', v => {
            if(v.length < 1) {
                return false;
            }
            else {
                return true;
            }
        });
    }, []);

    async function sendComment() {
        setCommentSending(true);
        let formValid = await formRef.current.isFormValid();
        if(!formValid) {
            swal(
                'Uh Oh!',
                'Make sure you filled out the comment form correctly!',
                'error',
            );
            setCommentSending(false);
        }
        else {
            let date = new Date();
            let months = ['Jan', 'Feb', 'Mar', 'Apr', 'May', 'Jun', 'July', 'Aug', 'Sept', 'Oct', 'Nov', 'Dec'];
            let day = date.getDate();
            let year = date.getFullYear();
            let month = date.getMonth();
            let dateString = `${months[month]} ${day}, ${year}`;

            let data = {
                uniqueCommentId: Date.now() + 'comment' + props.mainUser.uniqueUserId + props.post.uniqueUserId,
                uniqueUserId: props.mainUser.uniqueUserId,
                username: props.mainUser.username,
                dateString: dateString,
                text: comment,
                uniquePosterId: props.post.uniqueUserId,
                uniquePostId: props.post.uniquePostId,
            }

            return axios({
                method: 'POST',
                url: 'http://192.168.0.9:3001/api/add/comment',
                data: data,
                headers: {
                    'Content-Type': 'application/json',
                }
            }).then(response => {
                if(response.data.posts) {
                    props.dispatch({type: 'user/updateUser', payload: response.data.user});
                    props.dispatch({type: 'userPosts/updatePosts', payload: response.data.posts});
                    setCommentSending(false);
                    swal(
                        'Great!',
                        'Comment successfully sent!',
                        'success',
                    );
                }
                else {
                    swal(
                        'Uh Oh!',
                        'There was an error sending that comment! Please try again.',
                        'error',
                    );
                    setCommentSending(false);
                }
            }).catch(err => {
                console.log(err.message);
                swal(
                    'Uh Oh!',
                    'There was an error sending that comment! Please try again.',
                    'error',
                );
                setCommentSending(false);
            });
        }
    }

    return (
        <Card 
            className={classes.card} 
        >
            <CardHeader 
                title={
                    <Typography 
                        variant='h6' 
                        component='h6' 
                    >
                        {props.post.username}
                    </Typography>
                }
                subheader={
                    <Typography 
                        variant='subtitle1' 
                        component='span' 
                        color='textSecondary' 
                    >
                        {props.post.dateString}
                    </Typography>
                }
                avatar={
                    <Avatar 
                        src={`http://192.168.0.9:3001/api/get/avatar/by/id/${props.post.uniqueUserId}`}
                        alt={`${props.post.username}`}
                        title={`${props.post.username}`} 
                    />
                }
            />
            <CardContent>
                <Typography 
                    variant='body2' 
                    component='p' 
                >
                    {props.post.text}
                </Typography>
            </CardContent>
            <CardActions>
                <IconButton 
                    color='primary'
                >
                    <Icon  
                        path={props.post.likes.includes(props.mainUser) ? mdiThumbUp : mdiThumbUpOutline}
                        size={1} 
                        title='Post like button'
                        aria-label='Post like button' 
                    />
                    <div>
                        {props.post.likes.length > 0 &&
                            <Typography 
                                variant='body1' 
                                component='p' 
                            >
                                {props.post.likes.length}
                            </Typography>
                        }
                    </div>
                </IconButton>
                <IconButton 
                    color='primary'
                >
                    <Icon 
                        path={_.find(props.post.comments, comment => comment.uniqueUserId === props.mainUser.uniqueUserId) ? mdiComment : mdiCommentOutline} 
                        size={1}
                        title='Comment button' 
                        aria-label='Comment button' 
                    />
                    <div>
                        {props.post.comments.length > 0 &&
                            <Typography 
                                variant='body1' 
                                component='p' 
                            >
                                {props.post.comments.length}
                            </Typography>
                        }
                    </div>
                </IconButton>
                <div>
                    {props.post.uniqueUserId === props.mainUser.uniqueUserId &&
                        <IconButton 
                            color='primary' 
                        >
                            <Icon 
                                path={mdiDelete} 
                                size={1} 
                                title='Delete button' 
                                aria-label='Delete button' 
                            />
                        </IconButton>
                    }
                </div>
                <IconButton 
                    style={{
                        marginLeft: 'auto' 
                    }}
                    onClick={() => setExpanded(!expanded)}
                >
                    <Icon 
                        path={expanded ? mdiChevronUp : mdiChevronDown} 
                        size={1}
                        title={expanded ? 'Expand less' : 'Expand more'} 
                        aria-label={expanded ? 'Expand less' : 'Expand more'} 
                    />
                </IconButton>
            </CardActions>
            <Collapse 
                in={expanded} 
            >
                <Divider />
                <CardContent>
                    <ValidatorForm 
                        ref={formRef} 
                        onSubmit={e => e.preventDefault()} 
                    >
                        <TextValidator 
                            label='Comment' 
                            placeholder='Add a comment...' 
                            variant='outlined' 
                            color='primary' 
                            helperText='Add a comment' 
                            value={comment} 
                            onChange={e => setComment(e.target.value)} 
                            validator={['notEmpty']}
                            errorMessages={['You must enter some text to send a comment!']}
                            InputLabelProps={{
                                shrink: true,
                            }}
                            rows={4}
                            multiline 
                            fullWidth 
                        />
                        <Button 
                            color='primary' 
                            variant='outlined' 
                            startIcon={
                                <Icon 
                                    path={mdiSend} 
                                    size={1} 
                                    title='Send icon' 
                                />
                            }
                            aria-label='Send comment button' 
                            disabled={commentSending}
                            onClick={sendComment}
                        >
                            {commentSending ? <CircularProgress color='primary' /> : 'Send'}
                        </Button>
                    </ValidatorForm>
                </CardContent>
            </Collapse>
        </Card>
    );
}

function mapStateToProps(state) {
    return {
        mainUser: state.userReducer.user,
        posts: state.userPostsReducer.posts,
    };
}

export default connect(mapStateToProps)(UserPostsCard);
