import React, { useState, useEffect } from 'react';
import Grid from '@material-ui/core/Grid';
import Avatar from '@material-ui/core/Avatar';
import Typography from '@material-ui/core/Typography';
import { connect } from 'react-redux';
import axios from 'axios';
import { useHistory } from 'react-router-dom';
import swal from 'sweetalert';
import { makeStyles } from '@material-ui/core/styles';
import Hidden from '@material-ui/core/Hidden';
import Box from '@material-ui/core/Box';
import Container from '@material-ui/core/Container';

const useStyles = makeStyles((theme) => ({
    root: {
        marginTop: 20,
    },
    profileAvatar: {
        height: 60,
        width: 60,
    },
    profileAvatarLg: {
        height: 168,
        width: 168,
    },
    usernameDisplay: {
        marginLeft: 'auto',
       
    },
    statsInfo: {
        marginTop: 40,
        margin: 'auto',
    }
}));

function ProfilePage(props) {
    const history = useHistory(); //The history API to access the brower history. 
    const classes = useStyles(); //Custom styling.
    
    useEffect(() => {
        //First, if the user doesn't exist in state, re-route to the login page.
        if(!props.user) {
            history.push('/');
        }
        else if(props.user) {
            //Else, we make an axios call to request the user (an updated version of the user)
            //props.dispatch({type: 'ThemeChange', payload: props.user.profileTheme});
            return axios({
                method: 'GET',
                url: `http://192.168.0.9:3001/api/grab/user/${props.user.uniqueUserId}`,
            }).then(response => {
                if(response.data.user) {
                    props.dispatch({type: 'user/updateUser', payload: response.data.user});
                    props.dispatch({type: 'ThemeChange', payload: response.data.user.profileTheme});
                }
                else if(response.data === 'user not found') {
                    swal(
                        'Uh Oh!',
                        'We could not find your profile!',
                        'error',
                    );
                    history.push('/');
                }
            }).catch(err => {
                console.log(err.message);
                swal(
                    'Uh Oh!',
                    'There was an error retreiving your profile. Please try again!',
                    'error',
                );
                history.push('/');
            });
        }
    }, []);

    return (
        <Container 
            maxWidth='xl'
        >
            <Grid 
                className={classes.root}
                container 
            >
                <Grid 
                    item 
                    container 
                    xs={12}
                    spacing={5}
                >
                    <Grid 
                        item 
                        xs={2}
                        style={{
                            marginRight: 'auto',
                            marginTop: 20,
                        }}
                    >
                        {/* Below is the avatar for xs and sm screens at 128x128 pixels */}
                        <Hidden 
                            mdUp 
                        >
                            <Avatar 
                                className={classes.profileAvatar} 
                                src={`http://192.168.0.9:3001/api/get-photo/${props.user.avatar}`} 
                                title={`${props.user.username}`} 
                                alt={`${props.user.username}`} 
                            />
                        </Hidden>
                        {/* Below will be the avatar for medium screen and up devices */ }
                        <Hidden 
                            smDown 
                        >
                            <Avatar 
                                className={classes.profileAvatarLg}
                                src={`http://192.168.0.9:3001/api/get-photo/${props.user.avatar}`} 
                                title={`${props.user.username}`} 
                                alt={`${props.user.username}`} 
                            />
                        </Hidden>
                    </Grid>
                    <Grid 
                        item 
                        xs={6} 
                        style={{
                            marginTop: 20,
                        }}
                    >
                        <Typography 
                            variant='h4' 
                            component='h4'
                        >
                            {props.user.username}
                        </Typography>
                        <Hidden 
                            smDown 
                        >
                            <Grid 
                                item 
                                xs={12} 
                            >
                                <Grid 
                                    item 
                                    xs={4} 
                                >
                                    <Typography 
                                        variant='h6' 
                                        component='h6' 
                                    >
                                        Followers 
                                    </Typography>
                                    <Typography 
                                        variant='body1'
                                        component='p' 
                                    >
                                        {props.user.followers.length}
                                    </Typography>
                                </Grid>
                            </Grid>
                        </Hidden>
                    </Grid>
                </Grid>
            </Grid>
        </Container>
    );
}

function mapStateToProps(state) {
    return {
        user: state.userReducer.user,
        primary: state.userThemeReducer.primary,
    };
}

export default connect(mapStateToProps)(ProfilePage);
