import React, { useState, useEffect } from 'react';
import Grid from '@material-ui/core/Grid';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemAvatar from '@material-ui/core/ListItemAvatar';
import ListItemText from '@material-ui/core/ListItemText';
import Typography from '@material-ui/core/Typography';
import IconButton from '@material-ui/core/IconButton';
import Icon from '@mdi/react';
import { mdiCheckBold, mdiClose } from '@mdi/js';
import { connect } from 'react-redux';
import Backdrop from '@material-ui/core/Backdrop';
import CircularProgress from '@material-ui/core/CircularProgress';
import { useHistory } from 'react-router-dom';
import { makeStyles } from '@material-ui/core/styles';
import swal from 'sweetalert';
import axios from 'axios';
import Avatar from '@material-ui/core/Avatar';
import Paper from '@material-ui/core/Paper';

const useStyles = makeStyles(() => ({
    root: {
        marginTop: 100,
    },
    item: {
        margin: 'auto',
    },
    list: {
        margin: 'auto',
        maxWidth: 700,
    },
}));

function NotificationsComponent(props) {
    const [deletingNotification, setDeletingNotification] = useState(false); //Backdrop when we want to delete notification.
    const history = useHistory();
    const classes = useStyles();
    
    useEffect(() => {
        if(props.mainUser === null) {
            history.push('/');
        }
        else {
            return axios({
                method: 'GET',
                url: `http://192.168.0.9:3001/api/grab/user/${props.mainUser.uniqueUserId}`
            }).then(response => {
                props.dispatch({type: 'user/updateUser', payload: response.data.user});
                props.dispatch({type: 'ThemeChange', payload: response.data.user.profileTheme});
            }).catch(err => {
                console.log(err.message);
                swal(
                    'Uh Oh!',
                    'There was an error gathering your notifications! Please try again',
                    'error',
                );
                history.goBack(1);
            });
        }
    }, []);

    function utcToDateString(utc) {
        let date = new Date(utc);
        let months = ['Jan', 'Feb', 'Mar', 'Apr', 'May', 'Jun', 'Jul', 'Aug', 'Sept', 'Oct', 'Nov', 'Dec'];
        let month = months[date.getMonth()];
        let day = date.getDate();
        let year = date.getFullYear();
        return month + ' ' + day + ', ' + year;
    }

    function deleteNotification(uniqueNotificationId, link) {
        setDeletingNotification(true);
        let data = JSON.stringify({
            uniqueUserId: props.mainUser.uniqueUserId,
            uniqueNotificationId: uniqueNotificationId,
            link: link,
        });

        return axios({
            method: 'POST',
            url: 'http://192.168.0.9:3001/api/delete/notification',
            data: data,
            headers: {
                'Content-Type': 'application/json',
            },
        }).then(response => {
            setDeletingNotification(false);
            props.dispatch({type: 'user/updateUser', payload: response.data.user});
            swal(
                'Great!',
                'Notification successfully deleted!',
                'success',
            );
            if(response.data.link !== '') {
                history.push(response.data.link);
            }
        }).catch(err => {
            setDeletingNotification(false);
            console.log(err.message);
            swal(
                'Uh Oh!',
                'There was an error deleting that notification!',
                'error',
            );
        });
    }

    if(props.mainUser !== null) {
        return (
            <Grid 
                container 
                className={classes.root}
            >
                <Backdrop 
                    open={deletingNotification} 
                >
                    <CircularProgress 
                        color='primary' 
                    />
                </Backdrop>
                {props.mainUser.notifications.length > 0 &&
                    <Grid 
                        item 
                        xs={12} 
                    >
                        <List
                            className={classes.list} 
                        >
                            {props.mainUser.notifications.map((notification, index) => (
                                <div 
                                    key={index.toString()} 
                                >
                                    {notification.type === 'post comment' &&
                                        <Paper
                                            elevation={3}
                                        >
                                            <ListItem 
                                                style={{
                                                    cursor: 'pointer',
                                                    marginBottom: 20,
                                                }}
                                                onClick={e => deleteNotification(notification.uniqueNotificationId, notification.link)}
                                            >
                                                <ListItemAvatar>
                                                    <Avatar 
                                                        src={`http://192.168.0.9:3001/api/get/avatar/by/id/${notification.uniqueSenderId}`}
                                                        alt={`${notification.sender}`}
                                                        title={`${notification.sender}`}
                                                    />
                                                </ListItemAvatar>
                                                <ListItemText 
                                                    primary={
                                                        <Typography 
                                                            variant='h6' 
                                                            component='h6' 
                                                            align='center' 
                                                        >
                                                            {utcToDateString(notification.date)}
                                                        </Typography>
                                                    }
                                                    secondary={
                                                        <Typography 
                                                            variant='body1' 
                                                            component='p' 
                                                            align='center' 
                                                        >
                                                            {notification.msg}
                                                        </Typography>
                                                    }
                                                />
                                            </ListItem>
                                        </Paper>
                                    }
                                    {notification.type === 'post like' &&
                                        <Paper
                                            elevation={3}
                                        >
                                            <ListItem 
                                                style={{
                                                    cursor: 'pointer',
                                                    marginBottom: 20,
                                                }}
                                                onClick={e => deleteNotification(notification.uniqueNotificationId, notification.link)}
                                            >
                                                <ListItemAvatar>
                                                    <Avatar 
                                                        src={`http://192.168.0.9:3001/api/get/avatar/by/id/${notification.uniqueSenderId}`}
                                                        alt={`${notification.sender}`}
                                                        title={`${notification.sender}`}
                                                    />
                                                </ListItemAvatar>
                                                <ListItemText 
                                                    primary={
                                                        <Typography 
                                                            variant='h6' 
                                                            component='h6' 
                                                            align='center' 
                                                        >
                                                            {utcToDateString(notification.date)}
                                                        </Typography>
                                                    }
                                                    secondary={
                                                        <Typography 
                                                            variant='body1' 
                                                            component='p' 
                                                            align='center' 
                                                        >
                                                            {notification.msg}
                                                        </Typography>
                                                    }
                                                />
                                            </ListItem>
                                        </Paper>
                                    }
                                    {notification.type === 'new follower' &&
                                        <Paper
                                            elevation={3}
                                        >
                                            <ListItem 
                                                style={{
                                                    cursor: 'pointer',
                                                    marginBottom: 20,
                                                }}
                                                onClick={e => deleteNotification(notification.uniqueNotificationId, notification.link)}
                                            >
                                                <ListItemAvatar>
                                                    <Avatar 
                                                        src={`http://192.168.0.9:3001/api/get/avatar/by/id/${notification.uniqueSenderId}`}
                                                        alt={`${notification.sender}`}
                                                        title={`${notification.sender}`}
                                                    />
                                                </ListItemAvatar>
                                                <ListItemText 
                                                    primary={
                                                        <Typography 
                                                            variant='h6' 
                                                            component='h6' 
                                                            align='center' 
                                                        >
                                                            {utcToDateString(notification.date)}
                                                        </Typography>
                                                    }
                                                    secondary={
                                                        <Typography 
                                                            variant='body1' 
                                                            component='p' 
                                                            align='center' 
                                                        >
                                                            {notification.msg}
                                                        </Typography>
                                                    }
                                                />
                                            </ListItem>
                                        </Paper>
                                    }
                                    {notification.type === 'comment like' &&
                                        <Paper
                                            elevation={3}
                                        >
                                            <ListItem 
                                                style={{
                                                    cursor: 'pointer',
                                                    marginBottom: 20,
                                                }}
                                                onClick={e => deleteNotification(notification.uniqueNotificationId, notification.link)}
                                            >
                                                <ListItemAvatar>
                                                    <Avatar 
                                                        src={`http://192.168.0.9:3001/api/get/avatar/by/id/${notification.uniqueSenderId}`}
                                                        alt={`${notification.sender}`}
                                                        title={`${notification.sender}`}
                                                    />
                                                </ListItemAvatar>
                                                <ListItemText 
                                                    primary={
                                                        <Typography 
                                                            variant='h6' 
                                                            component='h6' 
                                                            align='center' 
                                                        >
                                                            {utcToDateString(notification.date)}
                                                        </Typography>
                                                    }
                                                    secondary={
                                                        <Typography 
                                                            variant='body1' 
                                                            component='p' 
                                                            align='center' 
                                                        >
                                                            {notification.msg}
                                                        </Typography>
                                                    }
                                                />
                                            </ListItem>
                                        </Paper>
                                    }
                                    {notification.type === 'follower request' &&
                                        <Paper
                                            elevation={3}
                                        >
                                            <ListItem
                                                style={{
                                                    marginBottom: 20,
                                                }}
                                            >
                                                <ListItemAvatar>
                                                    <Avatar 
                                                        src={`http://192.168.0.9:3001/api/get/avatar/by/id/${notification.uniqueSenderId}`}
                                                        alt={`${notification.sender}`}
                                                        title={`${notification.sender}`}
                                                    />
                                                </ListItemAvatar>
                                                <ListItemText 
                                                    primary={
                                                        <Typography 
                                                            variant='h6' 
                                                            component='h6' 
                                                            align='center' 
                                                        >
                                                            {utcToDateString(notification.date)}
                                                        </Typography>
                                                    }
                                                    secondary={
                                                        <Typography 
                                                            variant='body1' 
                                                            component='p' 
                                                            align='center' 
                                                        >
                                                            {notification.msg}
                                                        </Typography>
                                                    }
                                                />
                                            </ListItem>
                                            <IconButton 
                                                style={{
                                                    color: 'rgb(0, 128, 0)',
                                                }}
                                                aria-label='Accept follower request button'
                                            >
                                                <Icon 
                                                    path={mdiCheckBold} 
                                                    size={1}
                                                    title="Acccept follow request" 
                                                    aria-label='Follow request accept icon' 
                                                />
                                            </IconButton>
                                            <IconButton 
                                                style={{
                                                    color: 'rgb(255, 0, 0)'
                                                }}
                                                aria-label='Deny request button'
                                            >
                                                <Icon 
                                                    path={mdiClose} 
                                                    size={1}
                                                    title='Deny request icon'
                                                    aria-label='Deny request icon' 
                                                />
                                            </IconButton>
                                        </Paper>  
                                    }
                                </div>
                            ))}
                        </List>
                    </Grid>
                }
                {props.mainUser.notifications.length < 1 &&
                   <Grid 
                        item
                        xs={12} 
                        style={{
                            textAlign: 'center',
                        }}
                    >
                         <Typography 
                            variant='h6' 
                            component='h6' 
                            align='center' 
                        >
                            No Notifications
                        </Typography>
                    </Grid>
                }
            </Grid>
        );
    }
    else {
        return (
            <Backdrop
                open={true} 
            >
                <CircularProgress color='primary' />
            </Backdrop>
        );
    }
}

function mapStateToProps(state) {
    return {
        mainUser: state.userReducer.user,
        primary: state.userThemeReducer.primary,
    };
}

export default connect(mapStateToProps)(NotificationsComponent);
