import React, { useState } from 'react';
import Card from '@material-ui/core/card';
import CardHeader from '@material-ui/core/CardHeader';
import CardMedia from '@material-ui/core/CardMedia';
import CardContent from '@material-ui/core/CardContent';
import CardActions from '@material-ui/core/CardActions';
import IconButton from '@material-ui/core/IconButton';
import Collapse from '@material-ui/core/Collapse';
import Avatar from '@material-ui/core/Avatar';
import Typography from '@material-ui/core/Typography';
import Icon from '@mdi/react';
import { mdiThumbUp, mdiThumbUpOutline, mdiComment, mdiCommentOutline, mdiDelete, mdiChevronDown, mdiChevronUp } from '@mdi/js';
import { makeStyles } from '@material-ui/core/styles';
import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button';
import CircularProgress from '@material-ui/core/CircularProgress';
import Divider from '@material-ui/core/Divider';
import { connect } from 'react-redux';
import { useHistory } from 'react-router-dom';
import swal from 'sweetalert';
import axios from 'axios';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemAvatar from '@material-ui/core/ListItemAvatar';
import ListItemText from '@material-ui/core/ListItemText';
import * as _ from 'underscore';

const useStyles = makeStyles(() => ({
    card: {
        maxWidth: 600,
        margin: 'auto',
    },
    media: {
        height: 0,
        padding: '56.25%',
    }
}));

function TextPostCardP(props) {
    const classes = useStyles();
    const [expand, setExpand] = useState(false); //Expansion variable and setter for comments in Collapse
    const [commentVal, setCommentVal] = useState(''); //This will hold the value of a comment a user posts on a post.
    const [commentSending, setCommentSending] = useState(false); //This will disable the send button if a comment is sending
    const [likeSending, setLikeSending] = useState(false); //Will disable like button when set to true.
    const [curPost, setPost] = useState(props.post);
    const [hidePost, setHide] = useState(false);
    function timeDifference(date2, dateString) {
        //This function will return whether or not we display something like "2 hrs ago" "20 min ago" or the date string for a post
        let date1 = Date.now();
        let difference = date1 - date2;
        let daysDifference = Math.floor(difference/1000/60/60/24);
        let hoursDifference = Math.floor(difference/1000/60/60);
        let minutesDifference = Math.floor(difference/1000/60);
        let secondsDifference = Math.floor(difference/1000);
        if(daysDifference > 0) {
            return dateString;
        }
        else if(hoursDifference > 0 && hoursDifference < 23) {
            return `${hoursDifference} hr ago`;
        }
        else if(minutesDifference > 0 && minutesDifference < 60) {
            return `${minutesDifference} min ago`;
        }
        else if(minutesDifference === 0) {
            return `${secondsDifference} secs ago`
        }
        else {
            return dateString;
        }
    }

    function sendComment() {
        //This function will handle sending a comment 
        setCommentSending(true);

        if(!commentVal) {
            swal(
                'Uh Oh',
                'You must enter some text to send a comment!',
                'error',
            );
            setCommentSending(false);
        }
        else {
            let months = ['Jan', 'Feb', 'Mar', 'Apr', 'May', 'Jun', 'Jul', 'Aug', 'Sept', 'Oct', 'Nov', 'Dec'];
            let myDate = new Date();
            let month = months[myDate.getMonth()];
            let day = myDate.getDate();
            let year = myDate.getFullYear();
            let dateString = `${month} ${day}, ${year}`;
            let data = JSON.stringify({
                uniqueCommentId: Date.now() + props.mainUser.uniqueUserId + props.post.uniquePostId,
                uniqueUserId: props.mainUser.uniqueUserId,
                username: props.mainUser.username,
                dateString: dateString,
                text: commentVal,
                receiver: props.post.uniqueUserId,
                uniquePostId: props.post.uniquePostId,
            });

            return axios({
                method: 'POST',
                url: 'http://192.168.0.9:3001/api/add/comment',
                data: data,
                headers: {
                    'Content-Type': 'application/json',
                },
            }).then(response => {
                if(response.data.comments) {
                    swal(
                        'Great!',
                        'Comment successfully added',
                        'success',
                    );
                    props.post.comments = response.data.comments;
                    setPost(response.data.post);
                    setCommentSending(false);
                    setCommentVal('');
                }
                else {
                    swal(
                        'Uh Oh!',
                        'There was an error sending that comment! Please try again.',
                        'error',
                    );
                }
            }).catch(err => {
                console.log(err.message);
                swal(
                    'Uh Oh!',
                    'There was an error sending that comment! Please try again.',
                    'error',
                );
                setCommentSending(false);
            });
        }
    }

    function handleLike() {
        //This function will handle either liking or unliking a post when the user presses the like Button 
        setLikeSending(true);
        let likeStatus;
        if(curPost.likes.includes(props.mainUser.uniqueUserId)) {
            likeStatus = 'unlike';
        }
        else {
            likeStatus = 'like';
        }

        let data = JSON.stringify({
            uniquePostId: props.post.uniquePostId,
            uniquePosterId: props.post.uniqueUserId,
            uniqueLikerId: props.mainUser.uniqueUserId,
            likeStatus: likeStatus,
        });

        return axios({
            method: 'POST',
            url: 'http://192.168.0.9:3001/api/handle/post/like',
            data: data,
            headers: {
                'Content-Type': 'application/json',
            },
        }).then(response => {
            setLikeSending(false);
            if(response.data.user) {
                props.dispatch({type: 'user/updateUser', payload: response.data.user});
            }
            if(response.data.likeStatus === 'unliked') {
                setPost(response.data.post);
                swal(
                    'Great!',
                    'Post successfully unliked!',
                    'success',
                );
            }
            else if(response.data.likeStatus === 'liked') {
                setPost(response.data.post);
                swal(
                    'Great!',
                    'Post successfully liked!',
                    'success',
                );
            }
        }).catch(err => {
            console.log(err.message);
            setLikeSending(false);
            if(likeStatus === 'like') {
                swal(
                    'Uh Oh!',
                    'There was an error liking that post!',
                    'error',
                );
            }
            else if(likeStatus === 'unlike') {
                swal(
                    'Uh Oh!',
                    'There was an error unliking that post!',
                    'error',
                );
            }
        });
    }

    return (
        <Card 
            className={classes.card} 
            id={curPost.uniquePostId}
            hidden={hidePost}
        >
            <CardHeader 
                title={
                    <Typography 
                        variant='h6' 
                        component='h6' 
                    >
                        {props.user.username}
                    </Typography>
                }
                subheader={
                    <Typography 
                        variant='subtitle1' 
                        component='span'
                        color='textSecondary'
                    >
                        {timeDifference(curPost.utcTime, curPost.dateString)}
                    </Typography>
                }
                avatar={
                    <Avatar 
                        src={curPostost.context === 'personal' ? `http://192.168.0.9:3001/api/get-photo/${props.user.avatar}` : 'path to community photo upload'}
                        title={props.user.username} 
                        alt={props.user.username}
                    />
                }
            />
            <CardContent>
                {curPost.text}
            </CardContent>
            <CardActions>
                <IconButton 
                    color='primary' 
                    onClick={handleLike}
                >
                    <Icon 
                        path={curPost.likes.includes(props.mainUser.uniqueUserId) ? mdiThumbUp : mdiThumbUpOutline} 
                        size={1} 
                        aria-label='Like Button' 
                        disabled={likeSending}
                    />
                    <div>
                        {curPost.likes.length > 0 &&
                            <div>
                                {curPost.likes.length} 
                            </div>
                        }
                    </div>
                </IconButton>
                <IconButton 
                    color='primary' 
                >
                    <Icon 
                        path={_.find(curPost.comments, comment => comment.uniqueUserId === props.mainUser.uniqueUserId) ? mdiComment : mdiCommentOutline} 
                        size={1} 
                        aria-label='Comment Button' 
                    />
                    <div>
                        {curPost.length > 0 &&
                            <div>
                                {curPost.comments.length} 
                            </div>
                        }
                    </div>
                </IconButton>
                <div>
                    {curPost.uniqueUserId === props.user.uniqueUserId &&
                        <IconButton 
                            color='primary' 
                            aria-label='Delete post button'
                        >
                            <Icon 
                                path={mdiDelete} 
                                size={1} 
                                aria-label='Delete Icon' 
                            />
                        </IconButton>
                    }
                </div>
                <IconButton 
                    style={{
                        marginLeft: 'auto',
                    }}
                    onClick={() => setExpand(!expand)}
                >
                    <Icon 
                        path={expand ? mdiChevronUp : mdiChevronDown} 
                        size={1} 
                        aria-label={expand ? 'Close comments' : 'Expand comments'} 
                    />
                </IconButton>
            </CardActions>
            <Collapse 
                in={expand} 
            >
                <Divider />
                <CardContent>
                    {props.mainUser !== null &&
                        <div>
                            <TextField 
                                label='Comment' 
                                placeholder='Comment...'
                                value={commentVal}
                                onChange={e => setCommentVal(e.target.value)}
                                rows={3} 
                                color='primary'
                                variant='outlined' 
                                rows={4} 
                                multiline
                                fullWidth 
                                disabled={commentSending}
                            />
                            <Button 
                                variant='outlined' 
                                color='primary' 
                                style={{
                                    marginTop: 20,
                                }}
                                disabled={commentSending}
                                onClick={sendComment}
                            >
                                {commentSending ? <CircularProgress color='primary' /> : 'Send'}
                            </Button>
                        </div>
                    }
                    <Divider />
                    <List>
                        {curPost.comments.map((comment, index) => (
                            <div 
                                key={index} 
                            >
                                <ListItem
                                    id={comment.uniqueCommentId} 
                                    alignItems='flex-start'
                                >
                                    <ListItemAvatar>
                                        <Avatar 
                                            src={`http://192.168.0.9:3001/api/get/avatar/by/id/${comment.uniqueUserId}`}
                                            alt={`${comment.username}`}
                                            title={`${comment.username}`} 
                                        />
                                    </ListItemAvatar>
                                    <ListItemText 
                                        primary={
                                            <React.Fragment>
                                                <Typography 
                                                    variant='h6' 
                                                    component='h6' 
                                                >
                                                    {comment.username}
                                                </Typography>
                                                <Typography 
                                                    variant='subtitle2' 
                                                    component='small' 
                                                    color='textSecondary'
                                                >
                                                    {timeDifference(comment.utcTime, comment.dateString)}
                                                </Typography>
                                            </React.Fragment>
                                        }
                                        secondary={
                                            <Typography 
                                                variant='body1'
                                                component='p'
                                            >
                                                {comment.text}
                                            </Typography>
                                        }
                                    />
                                </ListItem>
                                {index < curPost.comments.length - 1 &&
                                    <Divider />
                                }
                            </div>
                        ))}
                    </List>
                </CardContent>
            </Collapse>
        </Card>
    );
}

function mapStateToProps(state) {
    return {
        mainUser: state.userReducer.user,
    }
}

export default connect(mapStateToProps)(TextPostCardP);
